(79, '2016-03-16 00:00:00', 'English for beginners [who, which, what, where]', 'Complete the questions', '{\"exercises\":[{\"exercise\":\"... is your name ? I\'m Greg.\",\"answers\":[\"Which\",\"What\",\"Who\",\"Where\"],\"true\":\"What\"},{\"exercise\":\"... are you from?  Germany\",\"answers\":[\"Which\",\"Who\",\"What\",\"Where\"],\"true\":\"Where\"},{\"exercise\":\"... are you?  I\'m Chris.\",\"answers\":[\"What\",\"Who\",\"Which\",\"Where\"],\"true\":\"Who\"},{\"exercise\":\"... of these colours do you like? Green.\",\"answers\":[\"What\",\"Which\",\"Who\",\"Where\"],\"true\":\"Which\"},{\"exercise\":\"... do you live? Paris.\",\"answers\":[\"What\",\"Which\",\"Who\",\"Where\"],\"true\":\"Where\"},{\"exercise\":\"... do you do? I\'m doctor.\",\"answers\":[\"Who\",\"Which\",\"What\",\"Where\"],\"true\":\"What\"},{\"exercise\":\"... one do you prefer? The green one.\",\"answers\":[\"What\",\"Who\",\"Which\",\"Where\"],\"true\":\"Which\"},{\"exercise\":\"... is that? That is Michael.\",\"answers\":[\"Who\",\"What\",\"Which\",\"Where\"],\"true\":\"Who\"},{\"exercise\":\"... do you go on holiday?  To Milan.\",\"answers\":[\"What\",\"Where\",\"Which\",\"What\"],\"true\":\"Where\"},{\"exercise\":\"... can I do? Nothing.\",\"answers\":[\"What\",\"Where\",\"Which\",\"Who\"],\"true\":\"What\"}]}', 6, 2, 0, '', 'A1'),
